package programs;

public class LargestOfThreeIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=50,b=25,c=55;
		if((a>b)&&(a>c))
		{	
		System.out.println("A is largest=" +a);
		}
		else if((b>a)&&(b>c))
		{
		System.out.println("B is largest=" +b);
		}
		else
		{
		System.out.println("C is largest="+c);
		}

	}

}
